/**
 * Error thrown when a chunk index is invalid (negative, non-integer, exceeds bounds).
 */
declare class InvalidChunkIndexError extends Error {
    readonly index: number;
    constructor(index: number, reason: string);
}
/**
 * Error thrown when a duplicate chunk index is provided.
 */
declare class DuplicateChunkIndexError extends Error {
    readonly index: number;
    constructor(index: number);
}

export { DuplicateChunkIndexError, InvalidChunkIndexError };
