import { AccountAddress, AccountAddressInput } from '@aptos-labs/ts-sdk';
import { BlobName } from './layout.js';
import 'zod';

/**
 * Represents a blob on the Shelby network.
 */
interface ShelbyBlob {
    /**
     * The account namespace the blob is stored in (e.g. "0x1").
     */
    account: AccountAddress;
    /**
     * The name of the blob (e.g. "foo/bar").
     */
    name: string;
    /**
     * A readable stream of the blob data.
     */
    readable: ReadableStream;
    /**
     * The length of the blob in bytes.
     */
    contentLength: number;
}
/**
 * Creates a key for a blob.
 *
 * @param account - The account namespace the blob is stored in (e.g. "0x1")
 * @param blobName - The name of the blob (e.g. "foo/bar")
 * @returns The key for the blob.
 *
 * @example
 * ```typescript
 * // "0x1.foo/bar.txt"
 * const key = createBlobKey({
 *   account: AccountAddress.fromString("0x1"),
 *   blobName: "foo/bar.txt",
 * });
 * ```
 */
declare const createBlobKey: (params: {
    account: AccountAddressInput;
    blobName: BlobName;
}) => string;

export { type ShelbyBlob, createBlobKey };
